import './App.css';
import Navbar from './navbar';
import Landing from './landingpage';
import { useEffect, useState } from 'react';
import OtpModal from './otp';
import Homeview from './homeview';
import { MakeAuthenticatedRequest } from './APIHelper';

function App() {
    const [signup, setSignup] = useState(false);
    const [signin, setSignin] = useState(true);
    const [signoff, setSignoff] = useState(false);
    const [isOtpModalOpen, setOtpModalOpen] = useState(false);
    const [landing, setLanding] = useState(true);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [Id, setId] = useState('');
    const [taskList, setTaskList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [refresh, setRefresh] = useState(false);

    const checkAuthStatus = async () => {
        try {
            const response = await MakeAuthenticatedRequest('http://127.0.0.1:8000/');
            if (response.status === 200) {
                setIsLoggedIn(true);
                setSignoff(true);
                setSignin(false);
                setSignup(false);
                setLanding(false);
                setTaskList(response.data);
            } else {
                throw new Error('Token invalid');
            }
        } catch (error) {
            console.error('Error checking login status:', error);
            setIsLoggedIn(false);
            setSignin(true);
            setSignup(false);
            setSignoff(false);
            setLanding(true);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
      checkAuthStatus();
      setRefresh(false);
      console.log('Refresh state changed:', refresh);
    }, [refresh]);

    const toggleSignup = () => {
        setSignup(false);
        setSignin(true);
    };

    const toggleSignin = () => {
        setSignup(true);
        setSignin(false);
    };

    return (
        <div className="App">
            {loading ? (
                <div className="d-flex justify-content-center align-items-center vh-100">
                    <h4>Loading...</h4>
                </div>
            ) : (
                <>
                    <Navbar 
                        signup={signup} 
                        signin={signin} 
                        signoff={signoff} 
                        toggleSignup={toggleSignup} 
                        toggleSignin={toggleSignin}
                        setIsLoggedIn={setIsLoggedIn} 
                        setSignoff={setSignoff}       
                        setSignin={setSignin}         
                        setSignup={setSignup}         
                        setLanding={setLanding}
                    />
                    {landing && (
                        <Landing 
                            signup={signup} 
                            signin={signin} 
                            setOtpModalOpen={setOtpModalOpen} 
                            setId={setId}
                        />
                    )}
                    {isLoggedIn ? (
                        <Homeview taskList={taskList} setRefresh={setRefresh} handleTaskCreation={handleTaskCreation}/>
                    ) : (
                        <OtpModal 
                            isOpen={isOtpModalOpen} 
                            onClose={() => setOtpModalOpen(false)} 
                            Id={Id} 
                            setIsLoggedIn={setIsLoggedIn} 
                            setSignoff={setSignoff} 
                            setSignin={setSignin} 
                            setSignup={setSignup} 
                            setLanding={setLanding} 
                            setRefresh={setRefresh} // Pass setRefresh
                        />
                    )}
                </>
            )}
        </div>
    );
}

export default App;
