import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Importing the calendar component
import 'react-calendar/dist/Calendar.css'; // Importing calendar styles

function CollapsibleCard({ task }) {
    const [isOpen, setIsOpen] = useState(false);
    const [dueDate, setDueDate] = useState(new Date(task.dueDate)); // Initialize with task's due date
    const [showCalendar, setShowCalendar] = useState(false); // State to toggle calendar visibility
    const [startTime, setStartTime] = useState(task.startTime || '');
    const [endTime, setEndTime] = useState(task.startTime || '');

    const toggleCard = () => {
        setIsOpen(!isOpen);
    };

    const handleDateChange = (date) => {
        setDueDate(date);
        setShowCalendar(false); // Hide calendar after date selection
        console.log("Selected Due Date: ", date); // Log the selected date
    };

    const handleStartTimeChange = (e) => {
        setStartTime(e.target.value); // Update the start time state
    };

    return (
        <div className="card">
            <div className="card-header" onClick={toggleCard}>
                <h3 className="task-name">{task.name}</h3>
                <span className={`arrow ${isOpen ? 'open' : ''}`}>â–¼</span>
            </div>
            {isOpen && (
                <div className="card-body">
                    <div className='desc_div'>
                        <p><strong>Description:</strong> {task.description}</p>
                    </div>
                    <div className='meta_div'>
                        <p>
                            <strong>Due Date:</strong> 
                            <span  onClick={() => setShowCalendar(!showCalendar)} style={{ cursor: 'pointer', color: 'blue',paddingLeft:'10px' }}>
                                {dueDate.toDateString()}
                            </span>
                        </p>
                        {showCalendar && (
                            <Calendar
                                onChange={handleDateChange}
                                value={dueDate}
                                className="calendar-view-container"
                            />
                        )}
                    </div>
                    <div className='meta_div'>
                        <p><strong>Start Time:</strong> 
                            <input 
                                type="time" 
                                value={startTime} 
                                onChange={handleStartTimeChange} 
                                className="time-input" 
                            />
                        </p>
                    </div>
                    <div className='meta_div'>
                        <p><strong>End Time:</strong> {task.endTime}</p>
                    </div>
                    <div className="button-group d-flex justify-content-between">
                        <button className="btn btn-primary fw-bold">Edit Task</button>
                        <button className="btn btn-danger fw-bold">Delete Task</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CollapsibleCard;
