import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Import react-calendar
import 'react-calendar/dist/Calendar.css'; // Import calendar's CSS

function CalendarView() {
    const [selectedDate, setSelectedDate] = useState(new Date());

    const handleDateChange = (date) => {
        setSelectedDate(date);
        console.log('Selected Date:', date);
    };

    return (
        <div className='calender border'>
            <div className="calendar-view-container" style={containerStyle}>
                <h2 style={headingStyle}>Select a Date</h2>
                <Calendar
                    onChange={handleDateChange}
                    value={selectedDate}
                />
                <p style={dateTextStyle}>
                    <strong>Selected Date:</strong> {selectedDate.toDateString()}
                </p>
            </div>
        </div>
    );
}

// Styles (optional, you can replace them with CSS or Bootstrap)
const containerStyle = {
    width: '300px',
    margin: '0 auto',
    textAlign: 'center',
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
};

const headingStyle = {
    fontSize: '1.5rem',
    marginBottom: '20px',
};

const dateTextStyle = {
    marginTop: '20px',
    fontSize: '1rem',
};

export default CalendarView;
