import './App.css';
import Navbar from './navbar';
import Landing from './landingpage';
import { useEffect, useState } from 'react';
import OtpModal from './otp';
import Homeview from './homeview';
import axios from 'axios'; 

function App() {
  const [signup, setSignup] = useState(false);
  const [signin, setSignin] = useState(true);
  const [signoff, setSignoff] = useState(false);
  const [isOtpModalOpen, setOtpModalOpen] = useState(false);
  const [landing, setLanding] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false); 
  const [Id,setId] = useState('');

  useEffect(() => {
    // Function to get the value of a specific cookie by name
    const getCookie = (name) => {
        const value = `; ${document.cookie}`;
        const parts = value.split(`; ${name}=`);
        if (parts.length === 2) return parts.pop().split(';').shift();
    };

    // Check if the session cookie is present
    const sessionId = getCookie('sessionid');
    if (sessionId) {
        // Make a request to the Django backend to confirm the session
        axios.get('http://127.0.0.1:8000/', { withCredentials: true })
            .then((response) => {
                if (response.status === 200) {
                    setIsLoggedIn(true); 
                } else {
                    setIsLoggedIn(false); 
                    setSignin(true);
                    setSignup(false);
                    setSignoff(false);
                }
            })
            .catch((error) => {
                console.error('Error checking login status:', error);
                setIsLoggedIn(false);
            });
    } else {
        setIsLoggedIn(false);
        setSignin(true);
        setSignup(false);
        setSignoff(false);
    }
}, []);

  const toggleSignup = () => {
    setSignup(true);
    setSignin(false);
  };

  const toggleSignin = () => {
    setSignup(false);
    setSignin(true);
  };

  const toggleLanding = () => {
    setSignup(true);
    setSignin(true);
    setSignoff(true);
  };

  return (
    <div className="App">
      <Navbar 
        signup={signup} 
        signin={signin} 
        signoff={signoff} 
        toggleSignup={toggleSignup} 
        toggleSignin={toggleSignin} 
      />
      {landing && <Landing signup={signup} signin={signin} setOtpModalOpen={setOtpModalOpen} setId={setId}/>}
      {isLoggedIn ? <Homeview /> : <OtpModal isOpen={isOtpModalOpen} onClose={() => setOtpModalOpen(false)} Id={Id} />}
    </div>
  );
}

export default App;
