import logo from './logo.svg';
import './App.css';
import Navbar from './navbar';
import Signup from './signup';
import Signin from './signin';
import Sidebanner from './banner';
import { useState } from 'react';

function App() {
  const [signup, setSignup] = useState(true);
  const [signin, setSignin] = useState(false);
  const [signoff, setSignoff] = useState(false);

  const toggleSignup = () => {
    setSignup(true);
    setSignin(false);
  };

  const toggleSignin = () => {
    setSignup(false);
    setSignin(true);
  };

  return (
    <div className="App">
      <Navbar signup={signup} signin={signin} signoff={signoff} toggleSignup={toggleSignup} toggleSignin={toggleSignin} />
      <div className='main_div border'>
        <Sidebanner/>
        {signup && <Signup />}
        {signin && <Signin />}
      </div>
    </div>
  );
}

export default App;
