import axios from 'axios';
import React, { useState } from 'react';

const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';')[0];
    return null;
};

function Signup({ setOtpModalOpen, setId }) {
    const [clickDisable, setClickDisable] = useState(false);
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        email: '',
        username: '',
        password: '',
        confirm_password: '',
        gender: '',
        agreeToTerms: false,
    });

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (formData.password !== formData.confirm_password) {
            alert('Passwords do not match!');
            return;
        }
        try {
            setClickDisable(true);
            setId(formData.email);

            // Get the CSRF token
            const csrfToken = getCookie('csrftoken');

            const response = await axios.post(
                'http://127.0.0.1:8000/signup/',
                formData,
                {
                    withCredentials: true,
                    headers: {
                        'X-CSRFToken': csrfToken, // Include CSRF token here
                    },
                }
            );

            console.log('Signup successful:', response.data);
            alert(response.data.message);
            setOtpModalOpen(true);
        } catch (error) {
            console.error(error.response?.data?.message);
            let error_message = "";
            if (error.response?.data) {
                const errorData = error.response.data.message;
                for (let key in errorData) {
                    if (Array.isArray(errorData[key])) {
                        error_message += `${errorData[key].join(', ')}\n`;
                    }
                }
            } else {
                error_message = 'An error occurred during signup.';
            }
            alert(error_message);
        } finally {
            setClickDisable(false);
        }
    };

    return (
        <div className="col container custom_signup">
            <form className="row g-3" onSubmit={handleSubmit}>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">First Name</div>
                    <input
                        type="text"
                        className="form-control"
                        name="first_name"
                        value={formData.first_name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">Last Name</div>
                    <input
                        type="text"
                        className="form-control"
                        name="last_name"
                        value={formData.last_name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">Email</div>
                    <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">Username</div>
                    <input
                        type="text"
                        className="form-control"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">Password</div>
                    <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">Confirm Password</div>
                    <input
                        type="password"
                        className="form-control"
                        name="confirm_password"
                        value={formData.confirm_password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">Gender</div>
                    <select
                        name="gender"
                        className="form-select"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Choose...</option>
                        <option value="M">Male</option>
                        <option value="F">Female</option>
                        <option value="O">Other</option>
                    </select>
                </div>
                <div className="col-12">
                    <div className="form-check text-start fw-bold">
                        <input
                            className="form-check-input border"
                            type="checkbox"
                            name="agreeToTerms"
                            checked={formData.agreeToTerms}
                            onChange={handleChange}
                            required
                        />
                        <label className="form-check-label text-start" htmlFor="gridCheck">
                            I agree 
                        </label>
                    </div>
                </div>
                <div className="col-12 text-start">
                    <button 
                        type="submit" 
                        className="btn btn-primary col-12" 
                        disabled={clickDisable} // Disable button on click
                    >
                        {clickDisable ? 'Signing Up...' : 'Sign Up'}
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Signup;
