import axios from 'axios';

function Navbar({ signup, signin, signoff, toggleSignup, toggleSignin, setIsLoggedIn, setSignoff, setSignin, setSignup, setLanding }) {
    // Function to get the JWT token from local storage
    const getJwtToken = () => {
        return localStorage.getItem('access_token');
    };

    const handleLogout = async () => {
        try {
            await axios.post('http://127.0.0.1:8000/logout/', {}, {
                headers: {
                    Authorization: `Bearer ${getJwtToken()}`, // Retrieve the JWT token
                },
            });

            // Clear the access token from local storage
            localStorage.removeItem('access_token');

            // Update state to reflect logout
            setIsLoggedIn(false);
            setSignoff(false);
            setSignin(true);
            setSignup(false);
            setLanding(true);
        } catch (error) {
            console.error('Error logging out:', error);
            // Optionally show a message to the user about the error
        }
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-body-tertiary">
                <div className="container-fluid">
                    <a className="navbar-brand brand fw-bold fs-3" href="#">
                        To-do-LIST
                    </a>
                    {signin && (
                        <a 
                            className="nav-link active fw-bold fs-5 brand-sub ps-4" 
                            aria-current="page" 
                            href="#"
                            onClick={toggleSignin}
                        >
                            Sign In
                        </a>
                    )}
                    {signup && (
                        <a 
                            className="nav-link active fw-bold fs-5 brand-sub ps-4" 
                            aria-current="page" 
                            href="#"
                            onClick={toggleSignup}
                        >
                            Sign Up
                        </a>
                    )}
                    {signoff && (
                        <a 
                            className="nav-link active fw-bold fs-5 ps-4"
                            aria-current="page" 
                            href="#" 
                            onClick={handleLogout} // Call handleLogout on click
                        >
                            Log Out
                        </a>
                    )}
                </div>
            </nav>
        </div>
    );
}

export default Navbar;
