import React, { useState } from "react";
import axios from "axios";

function CollapsibleCard({ task, index }) {
  const date = new Date(task.task_created);
  const hours = date.getHours();
  const minutes = String(date.getMinutes()).padStart(2, "0");
  const ampm = hours >= 12 ? "PM" : "AM";
  const formattedHours = hours % 12 || 12;
  const format12Hour = `${date.getFullYear()}-${String(
    date.getMonth() + 1
  ).padStart(2, "0")}-${String(date.getDate()).padStart(
    2,
    "0"
  )} ${formattedHours}:${minutes} ${ampm}`;

  const [isOpen, setIsOpen] = useState(false);
  const [showCalendar, setShowCalendar] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  const [dueDate, setDueDate] = useState(new Date(task.dueDate));
  const [startTime, setStartTime] = useState(task.startTime || "00:00");
  const [endTime, setEndTime] = useState(task.endTime || "23:59");
  const [allDay, setAllDay] = useState(task.allDay || false);
  const [description, setDescription] = useState(task.description);
  const [taskname, setTaskname] = useState(task.task_name);

  const [detail, setDetail] = useState({});

  const getJwtToken = () => localStorage.getItem("access_token");

  const fetchDetail = async (taskId) => {
    try {
      const token = getJwtToken();
      if (!token) {
        throw new Error("No token found");
      }
      const response = await axios.get("http://127.0.0.1:8000/view/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
        params: { task_id: taskId },
      });
      if (response.status === 200) {
        setDetail(response.data);
        setDescription(response.data.task_desc || "");
        setDueDate(new Date(response.data.schedule_date));
        setStartTime(response.data.start_time || "00:00");
        setEndTime(response.data.end_time || "23:59");
        setAllDay(response.data.allDay || false);
        console.log(response.data);
      } else {
        throw new Error("Task fetch failed");
      }
    } catch (error) {
      console.error("Error fetching task details:", error.message);
    }
  };

  const toggleCard = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      fetchDetail(task.task_id);
    }
  };

  const handleDateChange = (date) => {
    setDueDate(date);
    setShowCalendar(false);
    console.log("Selected Due Date: ", date);
  };

  const handleStartTimeChange = (e) => {
    setStartTime(e.target.value);
  };

  const handleEndTimeChange = (e) => {
    setEndTime(e.target.value);
  };

  const handleAllDayChange = (e) => {
    const isChecked = e.target.checked;
    setAllDay(isChecked);
    if (isChecked) {
      setStartTime("00:00");
      setEndTime("23:59");
    } else {
      setStartTime(task.startTime || "");
      setEndTime(task.endTime || "");
    }
  };

  const handleEditToggle = () => {
    if (isEditing) {
      setDescription(detail.task_desc || task.description);
      setDueDate(new Date(detail.schedule_date || task.dueDate));
      setStartTime(detail.start_time || task.startTime || "00:00");
      setEndTime(detail.end_time || task.endTime || "23:59");
      setAllDay(detail.allDay || task.allDay || false);
      setShowCalendar(false);
    } else {
      setShowCalendar(true);
    }
    setIsEditing(!isEditing);
  };

  const handleSaveChanges = async () => {
    try {
      const token = getJwtToken();
      if (!token) {
        throw new Error("No token found");
      }

      const updatedData = {
        task_id: task.task_id,
        task_desc: description,
        schedule_date: dueDate.toISOString().split("T")[0],
        start_time: startTime,
        end_time: endTime,
        allDay: allDay,
      };

      const response = await axios.put(
        "http://127.0.0.1:8000/update/",
        updatedData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200) {
        console.log("Task updated successfully:", response.data);
        setIsEditing(false);
      } else {
        throw new Error("Task update failed");
      }
    } catch (error) {
      console.error("Error updating task:", error.message);
    }
  };

  return (
    <div className="card">
      <div
        className="card-header d-flex justify-content-between"
        onClick={toggleCard}>
        <div className="d-flex justify-content-between">
          {isEditing ? (
            <input
              type="text"
              value={description} // Updated to use the state variable
              onChange={(e) => setDescription(e.target.value)}
              className="description-input"
            />
          ) : (
            <h3 className="task-name flex-grow-1">{taskname}</h3>
          )}
        </div>
        <div className="d-flex justify-content-between">
          <div className="d-flex align-items-center">
            <h6 className="flex-grow-1 mb-0 ms-4">
              created on : {format12Hour}
            </h6>
          </div>
          <button className={`arrow btn btn-link ${isOpen ? "open" : ""}`}>
            â–¼
          </button>
        </div>
      </div>
      {isOpen && (
        <div className="card-body">
          <div className="desc_div">
            <p>
              <strong style={{ paddingRight: "10px" }}>Description:</strong>
              {isEditing ? (
                <input
                  type="text"
                  value={description} // Updated to use the state variable
                  onChange={(e) => setDescription(e.target.value)}
                  className="description-input"
                />
              ) : (
                description
              )}
            </p>
          </div>
          <div className="meta_div">
            <p>
              <strong>Due Date:</strong>
              <span
                style={{
                  cursor: isEditing ? "pointer" : "default",
                  paddingLeft: "20px",
                }}>
                {showCalendar ? (
                  <input
                    type="date"
                    value={dueDate.toISOString().split("T")[0]}
                    onChange={(e) => handleDateChange(new Date(e.target.value))}
                  />
                ) : (
                  dueDate.toDateString()
                )}
              </span>
            </p>
          </div>
          <div className="meta_div">
            <p>
              <strong style={{ paddingRight: "10px" }}>Start Time:</strong>
              {isEditing ? (
                <input
                  type="time"
                  value={startTime} // Use state for start time
                  onChange={handleStartTimeChange}
                  className="time-input"
                  disabled={allDay}
                />
              ) : (
                startTime
              )}
            </p>
          </div>
          <div className="meta_div">
            <p>
              <strong style={{ paddingRight: "20px" }}>End Time:</strong>
              {isEditing ? (
                <input
                  type="time"
                  value={endTime} // Use state for end time
                  onChange={handleEndTimeChange}
                  className="time-input"
                  disabled={allDay}
                />
              ) : (
                endTime
              )}
            </p>
          </div>
          <div className="meta_div">
            <p>
              <strong style={{ paddingRight: "20px" }}>All Day:</strong>
              {isEditing ? (
                <input
                  className="form-check-input border me-4"
                  type="checkbox"
                  id="allDayCheck"
                  checked={allDay}
                  onChange={handleAllDayChange}
                />
              ) : allDay ? (
                "Yes"
              ) : (
                "No"
              )}
            </p>
          </div>
          <div className="button-group d-flex justify-content-between">
            {isEditing ? (
              <div>
                <button
                  className="btn btn-success fw-bold ms-5"
                  onClick={handleSaveChanges}>
                  Save Changes
                </button>
                <button
                  className="btn btn-secondary fw-bold me-5"
                  onClick={handleEditToggle}>
                  Cancel
                </button>
              </div>
            ) : (
              <button
                className="btn btn-primary fw-bold"
                onClick={handleEditToggle}>
                Edit Task
              </button>
            )}
            <button className="btn btn-danger fw-bold">Delete Task</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CollapsibleCard;
