import React, { useEffect, useState } from "react";
import { MakeAuthenticatedRequest } from "./APIHelper";

function Sidebar({ setRefresh }) {
    const [userProfile, setUserProfile] = useState({
        name: "Loading...",
        username: "@Loading...",
        email: "Loading...",
        profilePic: "",
    });
    const [isDeleting, setIsDeleting] = useState(false); // State for loading spinner

    const getPersonalinfo = async () => {
        try {
            const response = await MakeAuthenticatedRequest('http://127.0.0.1:8000/info');
            if (response.status === 200) {
                setUserProfile(response.data);
            } else {
                throw new Error("User invalid");
            }
        } catch (error) {
            console.error("Error fetching info:", error);
        }
    };

    const deleteProfile = async () => {
        setIsDeleting(true); // Show loading spinner
        try {
            const response = await MakeAuthenticatedRequest('http://127.0.0.1:8000/deleteuser/', 'DELETE');

            if (response.status === 200) {
                localStorage.removeItem('access_token');
                localStorage.removeItem('refresh_token');
                alert(response.data.message);
                console.log(response.data);
                window.location.reload();
            } else {
                throw new Error("User deletion failed");
            }
        } catch (error) {
            console.error("Error deleting user:", error);
        } finally {
            setIsDeleting(false); // Hide loading spinner
        }
    };

    useEffect(() => {
        getPersonalinfo();
    }, []);

    return (
        <div className="sidebar d-flex flex-column vh-90">
            {isDeleting && <div className="overlay"></div>} {/* Dimmed background */}
            <div className="profile-pic flex-shrink-0">
                <img src="/images/bg.png" alt="Profile Pic" />
            </div>
            <div className="user-info flex-shrink-0">
                <h4 className="name">{userProfile.name}</h4>
                <p className="username">{userProfile.username}</p>
                <p className="email">{userProfile.email}</p>
            </div>
            <div className="d-flex flex-column flex-grow-1">
                <div className="mt-auto mb-5">
                    <button type="button" className="btn btn-danger fw-bold d-flex justify-content-center" onClick={deleteProfile}>DELETE PROFILE</button>
                </div>
            </div>
            {isDeleting && <div className="spinner">Deleting...</div>} {/* Loading spinner */}
        </div>
    );
}

export default Sidebar;
