import React, { useState } from 'react';
import 'react-calendar/dist/Calendar.css';

function CollapsibleCard({ task }) {
    const [isOpen, setIsOpen] = useState(false);
    const [dueDate, setDueDate] = useState(new Date(task.dueDate)); // Initialize with task's due date
    const [showCalendar, setShowCalendar] = useState(false); // State to toggle calendar visibility
    const [startTime, setStartTime] = useState(task.startTime || '');
    const [endTime, setEndTime] = useState(task.endTime || ''); // Initialize end time state
    const [taskName, setTaskName] = useState(task.name); // State for task name
    const [description, setDescription] = useState(task.description); // State for description
    const [isEditing, setIsEditing] = useState(false); // State for edit mode

    const toggleCard = () => {
        setIsOpen(!isOpen);
    };

    const handleDateChange = (date) => {
        setDueDate(date);
        setShowCalendar(false); // Hide calendar after date selection
        console.log("Selected Due Date: ", date); // Log the selected date
    };

    const handleStartTimeChange = (e) => {
        setStartTime(e.target.value);
    };

    const handleEndTimeChange = (e) => {
        setEndTime(e.target.value);
    };

    const handleEditToggle = () => {
        if (isEditing) {
            setTaskName(task.name);
            setDescription(task.description);
            setDueDate(new Date(task.dueDate)); 
            setStartTime(task.startTime || '');
            setEndTime(task.endTime || '');
            setShowCalendar(false); 
        }
        setIsEditing(!isEditing);
    };

    const handleSaveChanges = () => {
        console.log("Updated Task:", { taskName, description, dueDate, startTime, endTime });
        setIsEditing(false);
    };

    return (
        <div className="card">
            <div className="card-header border" onClick={toggleCard}>
                <div className="d-flex justify-content-between align-items-center border">
                    <h3 className="task-name flex-grow-1">{taskName}</h3>
                </div>
                <div className='d-flex flex-end border'>
                    <h6 className="task-id">{task.taskID}</h6>
                </div>    
                <span className={`arrow ${isOpen ? 'open' : ''}`}>▼</span>
            </div>
            {isOpen && (
                <div className="card-body">
                    <div className='desc_div'>
                        <p>
                            <strong style={{paddingRight: '10px'}}>Description:</strong>
                            {isEditing ? (
                                <input 
                                    type="text" 
                                    value={description} 
                                    onChange={(e) => setDescription(e.target.value)} 
                                    className="description-input" 
                                />
                            ) : (
                                `${description}`
                            )}
                        </p>
                    </div>
                    <div className="meta_div">
                        <p>
                            <strong>Due Date:</strong>
                            <span
                                onClick={isEditing ? () => setShowCalendar(showCalendar) : undefined}
                                style={{ cursor: isEditing ? 'pointer' : 'default', paddingLeft: '20px' }}
                            >
                                {showCalendar ? (
                                    <input
                                        type="date"
                                        value={dueDate.toISOString().split('T')[0]} // Format the date for input
                                        onChange={(e) => handleDateChange(new Date(e.target.value))}
                                    />
                                ) : (
                                    dueDate.toDateString() // Display the date when the calendar is not shown
                                )}
                            </span>
                        </p>
                    </div>
                    <div className='meta_div'>
                        <p><strong style={{paddingRight: '10px'}}>Start Time:</strong> 
                            {isEditing ? (
                                <input 
                                    type="time" 
                                    value={startTime} 
                                    onChange={handleStartTimeChange} 
                                    className="time-input" 
                                />
                            ) : (
                                startTime
                            )}
                        </p>
                    </div>
                    <div className='meta_div'>
                        <p><strong style={{paddingRight: '20px'}}>End Time:</strong> 
                            {isEditing ? (
                                <input 
                                    type="time" 
                                    value={endTime} 
                                    onChange={handleEndTimeChange} 
                                    className="time-input" 
                                />
                            ) : (
                                endTime
                            )}
                        </p>
                    </div>
                    <div className="button-group d-flex justify-content-between">
                        {isEditing ? (
                            <div>
                                <button className="btn btn-success fw-bold ms-5" onClick={handleSaveChanges}>Save Changes</button>
                                <button className="btn btn-secondary fw-bold me-5" onClick={handleEditToggle}>Cancel</button>
                            </div>
                        ) : (
                            <button className="btn btn-primary fw-bold" onClick={handleEditToggle}>Edit Task</button>
                        )}
                        <button className="btn btn-danger fw-bold">Delete Task</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CollapsibleCard;
