import axios from 'axios';
import React, { useState } from 'react';

function Signin({setOtpModalOpen,setEmail}) {
    const [credential,setCredential] = useState("email")
    const [formData, setFormData] = useState({
        email: '',
        username: '',
        password: '',
    });

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            setEmail(formData.email)
            const response = await axios.post('http://127.0.0.1:8000/signup/', formData, { withCredentials: true });
            console.log('Signup successful:', response.data);
            alert(response.data.message);
            setOtpModalOpen(true)
        } catch (error) {
            console.error(error.response.data.message);
            let error_message = "";
            if (error.response?.data) {
                const errorData = error.response.data.message;
                for (let key in errorData) {
                    if (Array.isArray(errorData[key])) {
                        error_message += `${errorData[key].join(', ')}\n`;
                    }
                }
            } else {
                error_message = 'An error occurred during signup.';
            }
            alert(error_message);
        }
    };

    return (
        <div className="col container custom_signin">
            <form className="row g-3">
                <div className="col-12">
                    <div className="form-label text-end fw-bold">
                        Email OR Username
                    </div>
                    { credential==="email" ?<input
                        type="email"
                        className="form-control"
                        id="email"
                        required
                    />:
                    <input
                        type="text"
                        className="form-control"
                        id="email"
                        required
                    />}
                </div>
                <div className="col-12">
                    <div className="form-label text-end fw-bold">
                        Password
                    </div>
                    <input
                        type="password"
                        className="form-control"
                        id="password"
                        required
                    />
                </div>
                <div className="col-12">
                    <div className="form-check text-start fw-bold">
                        <input className="form-check-input border" type="checkbox" id="gridCheck" required />
                        <label className="form-check-label text-start" htmlFor="gridCheck">
                            Check this
                        </label>
                    </div>
                </div>
                <div className="col-12 text-start">
                    <button type="submit" className="btn btn-primary col-12">
                        Sign in
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Signin;
