import React, { useState } from 'react';
import axios from 'axios';

function OtpModal({ isOpen, onClose, Id ,setIsLoggedIn, setSignoff, setSignin, setSignup}) {
    const [otp, setOtp] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("OTP Submitted:", otp);

        try {
            const response = await axios.get(`http://localhost:8000/verify-otp/`, {
                params: { cred: Id, otp: otp }
            });
            console.log('OTP verification successful:', response.data);
            alert('OTP verified successfully!');
            setIsLoggedIn(true);
            setSignoff(true);
            setSignin(false);
            setSignup(false);
            onClose(); 
        } catch (error) {
            console.error('Error during OTP verification:', error.response ? error.response.data : error.message);
            alert('Error verifying OTP. Please try again.');
        }
    };

    const handleClose = () => {
        setOtp(''); 
        onClose();
    };

    if (!isOpen) return null; 

    return (
        <div className="otp-modal-overlay">
            <div className="otp-modal">
                <h2>Enter OTP</h2>
                <h6>OTP sent to your email address</h6>
                <form className="d-flex flex-column" onSubmit={handleSubmit}>
                    <input
                        type="text"
                        maxLength={6}
                        value={otp}
                        onChange={(e) => setOtp(e.target.value)}
                        placeholder="Enter 6-digit OTP"
                        required
                        className="mb-3"
                    />
                    <div className="d-flex justify-content-between">
                        <button className="btn btn-primary fw-bold" type="button" onClick={handleClose}>
                            Close
                        </button>
                        <button className="btn btn-primary fw-bold" type="submit">
                            Verify
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default OtpModal;
