import React, { useState } from 'react';

function OtpModal({ isOpen, onClose }) {
    const [otp, setOtp] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("OTP Submitted:", otp);
        onClose(); // Close the modal after submitting
    };

    const handleClose = () => {
        setOtp(''); // Reset OTP when closing the modal
        onClose();
    };

    if (!isOpen) return null; 

    return (
        <div className="otp-modal-overlay">
            <div className="otp-modal">
                <h2>Enter OTP</h2>
                <form className='' onSubmit={handleSubmit}>
                    <input
                        type="text"
                        maxLength={6}
                        value={otp}
                        onChange={(e) => setOtp(e.target.value)}
                        placeholder="Enter 6-digit OTP"
                        required
                    />
                    <button className="btn btn-primary ml-3 fw-3" type="button" onClick={handleClose}>Close</button>
                    <button className="btn btn-primary ml-3" type="submit">Submit</button>
                </form>
            </div>
        </div>
    );
}

export default OtpModal;
