import React, { useState } from 'react';

function TaskForm({ task, handleChange, handleSubmit, handleClose }) {
    // State for managing all-day tasks
    const [allDay, setAllDay] = useState(task.allDay || false);

    // Handle change for the all-day checkbox
    const handleAllDayChange = (e) => {
        const isChecked = e.target.checked;
        setAllDay(isChecked);

        // Set default time values if checked
        if (isChecked) {
            // Here we assume you want to set task.timeStart and task.timeEnd as default
            handleChange({ target: { name: 'timeStart', value: '00:00' } });
            handleChange({ target: { name: 'timeEnd', value: '23:59' } });
        } else {
            // If unchecked, clear time values or reset to original (if needed)
            handleChange({ target: { name: 'timeStart', value: '' } });
            handleChange({ target: { name: 'timeEnd', value: '' } });
        }
    };

    return (
        <>
            <div className="modal show" style={{ display: 'block' }} >
                <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                        <div className="modal-header d-flex justify-content-between">
                            <h5 className="modal-title">Create Task</h5>
                            <button type="button" className="close" onClick={handleClose}>
                                <span>&times;</span>
                            </button>
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="modal-body">
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Task Name</label>
                                    <input
                                        type="text"
                                        id="taskName"
                                        name="name"
                                        value={task.name}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Description</label>
                                    <textarea
                                        id="taskDescription"
                                        name="description"
                                        value={task.description}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Schedule Date</label>
                                    <input
                                        type="date"
                                        id="taskDueDate"
                                        name="dueDate"
                                        value={task.dueDate}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Start Time</label>
                                    <input
                                        type="time"
                                        id="timeStart"
                                        name="timeStart"
                                        value={task.timeStart}
                                        onChange={handleChange}
                                        className="form-control"
                                        disabled={allDay} // Disable if allDay is checked
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>End Time</label>
                                    <input
                                        type="time"
                                        id="timeEnd"
                                        name="timeEnd"
                                        value={task.timeEnd}
                                        onChange={handleChange}
                                        className="form-control"
                                        disabled={allDay} // Disable if allDay is checked
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2 border'>All Day</label>
                                    <input
                                        type="checkbox"
                                        id="allDay"
                                        name="allDay"
                                        checked={allDay}
                                        onChange={handleAllDayChange}
                                        className="form-check-input"
                                    />
                                </div>
                            </div>
                            <div className="modal-footer d-flex justify-content-between">
                                <button type="button" className="btn btn-secondary" onClick={handleClose}>Cancel</button>
                                <button type="submit" className="btn btn-primary">Create Task</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default TaskForm;
