import './App.css';
import Navbar from './navbar';
import Landing from './landingpage';
import { useEffect, useState } from 'react';
import OtpModal from './otp';
import Homeview from './homeview';
import axios from 'axios';

function App() {
  const [signup, setSignup] = useState(false);
  const [signin, setSignin] = useState(true);
  const [signoff, setSignoff] = useState(false);
  const [isOtpModalOpen, setOtpModalOpen] = useState(false);
  const [landing, setLanding] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [Id, setId] = useState('');

  // // Corrected getCookie function
  // const getCookie = (name) => {
  //   const value = `; ${document.cookie}`;
  //   const parts = value.split(`; ${name}=`);
  //   if (parts.length === 2) return parts.pop().split(';')[0];
  //   return null;
  // };

  // // Fetch CSRF token from cookies
  // const csrfToken = getCookie('csrftoken');

  // useEffect(() => {
  //   const checkAuthStatus = async () => {
  //     try {
  //       console.log('CSRF Token:', csrfToken);  // For debugging

  //       // Ensure that sessionid and csrftoken cookies are sent by setting withCredentials to true
  //       const response = await axios.get('http://127.0.0.1:8000/', {
  //         withCredentials: true,  // Ensures cookies (sessionid) are sent
  //         headers: {
  //           'X-CSRFToken': csrfToken,  // Send CSRF token along with request
  //         },
  //       });

  //       if (response.status === 200) {
  //         const { isAuthenticated } = response.data;
  //         setIsLoggedIn(isAuthenticated);
  //         setSignoff(isAuthenticated);
  //         setSignin(!isAuthenticated);
  //         setSignup(false);
  //         setLanding(isAuthenticated);
  //       }
  //     } catch (error) {
  //       console.error('Error checking login status:', error);
  //       setIsLoggedIn(false);
  //       setSignin(true);
  //       setSignup(false);
  //       setSignoff(false);
  //       setLanding(true);
  //     }
  //   };

  //   checkAuthStatus();
  // }, [csrfToken]);  // Re-run this effect if csrfToken changes
  useEffect(() => {
    let isMounted = true;  // To ensure we don't set the token multiple times

    const getCsrfToken = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/csrf/', {
          withCredentials: true,
        });

        if (isMounted) {
          console.log('CSRF Token from Django:', response.data.csrfToken);
          document.cookie = `csrftoken=${response.data.csrfToken}; path=/`;
        }
      } catch (error) {
        console.error('Error fetching CSRF token:', error);
      }
    };

    getCsrfToken();

    return () => {
      isMounted = false;  // Clean up to avoid setting state after unmount
    };
  }, []);

  // Function to toggle signup
  const toggleSignup = () => {
    setSignup(false);
    setSignin(true);
  };

  // Function to toggle signin
  const toggleSignin = () => {
    setSignup(true);
    setSignin(false);
  };

  return (
    <div className="App">
      <Navbar 
        signup={signup} 
        signin={signin} 
        signoff={signoff} 
        toggleSignup={toggleSignup} 
        toggleSignin={toggleSignin} 
      />
      {landing && (
        <Landing 
          signup={signup} 
          signin={signin} 
          setOtpModalOpen={setOtpModalOpen} 
          setId={setId}
        />
      )}
      {isLoggedIn ? (
        <Homeview />
      ) : (
        <OtpModal 
          isOpen={isOtpModalOpen} 
          onClose={() => setOtpModalOpen(false)} 
          Id={Id} 
          setIsLoggedIn={setIsLoggedIn} 
          setSignoff={setSignoff} 
          setSignin={setSignin} 
          setSignup={setSignup} 
          setLanding={setLanding} 
        />
      )}
    </div>
  );
}

export default App;
