import './App.css';
import Navbar from './navbar';
import Landing from './landingpage';
import { useEffect, useState } from 'react';
import OtpModal from './otp';
import Homeview from './homeview';
import axios from 'axios'; 

function App() {
  const [signup, setSignup] = useState(false);
  const [signin, setSignin] = useState(true);
  const [signoff, setSignoff] = useState(false);
  const [isOtpModalOpen, setOtpModalOpen] = useState(false);
  const [landing, setLanding] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false); 
  const [Id,setId] = useState('');
  const csrfToken = getCookie('csrftoken');

  // const getCookie = (name) => {
  //   const value = `; ${document.cookie}`;
  //   const parts = value.split(`; ${name}=`);
  //   if (parts.length === 2) return parts.pop().split(';')[0];
  //   return null;
  // };

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/', {
          withCredentials: true,
          headers: {
            'X-CSRFToken': csrfToken,
          },
        });

        if (response.status === 200) {
          const { isAuthenticated } = response.data; // Adjust based on your response structure

          setIsLoggedIn(isAuthenticated);
          setSignoff(isAuthenticated);
          setSignin(!isAuthenticated);
          setSignup(false);
          setLanding(isAuthenticated);
        }
      } catch (error) {
        console.error('Error checking login status:', error);
        setIsLoggedIn(false);
        setSignin(true);
        setSignup(false);
        setSignoff(false);
        setLanding(true);
      }
    };
    checkAuthStatus();
  }, [csrfToken, setIsLoggedIn, setSignoff, setSignin, setSignup, setLanding]);

  const toggleSignup = () => {
    setSignup(false);
    setSignin(true);
  };

  const toggleSignin = () => {
    setSignup(true);
    setSignin(false);
  };

  return (
    <div className="App">
      <Navbar 
        signup={signup} 
        signin={signin} 
        signoff={signoff} 
        toggleSignup={toggleSignup} 
        toggleSignin={toggleSignin} 
      />
      {landing && <Landing signup={signup} signin={signin} setOtpModalOpen={setOtpModalOpen} setId={setId}/>}
      {isLoggedIn ? <Homeview /> : <OtpModal isOpen={isOtpModalOpen} onClose={() => setOtpModalOpen(false)} Id={Id} setIsLoggedIn ={setIsLoggedIn} setSignoff={setSignoff} setSignin={setSignin} setSignup={setSignup} setLanding={setLanding}/>}
    </div>
  );
}

export default App;
