import './App.css';
import Navbar from './navbar';
import Landing from './landingpage';
import { useEffect, useState } from 'react';
import OtpModal from './otp';
import Homeview from './homeview';
import axios from 'axios'; 

function App() {
  const [signup, setSignup] = useState(true);
  const [signin, setSignin] = useState(false);
  const [signoff, setSignoff] = useState(false);
  const [isOtpModalOpen, setOtpModalOpen] = useState(false);
  const [landing, setLanding] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false); 
  const [email,setEmail] = useState('');

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/', { withCredentials: true })
      .then((response) => {
        if (response.status === 200) {
          setIsLoggedIn(true); 
        } else {
          setIsLoggedIn(false); 
          setSignin(false);
          setSignup(true);
          setSignoff(false);
        } 
      })
      .catch((error) => {
        console.error('Error checking login status:', error);
        setIsLoggedIn(false);
      });
  }, []);

  const toggleSignup = () => {
    setSignup(true);
    setSignin(false);
  };

  const toggleSignin = () => {
    setSignup(false);
    setSignin(true);
  };

  const toggleLanding = () => {
    setSignup(true);
    setSignin(true);
    setSignoff(true);
  };

  return (
    <div className="App">
      <Navbar 
        signup={signup} 
        signin={signin} 
        signoff={signoff} 
        toggleSignup={toggleSignup} 
        toggleSignin={toggleSignin} 
        setEmail={setEmail}
      />
      {landing && <Landing signup={signup} signin={signin} setOtpModalOpen={setOtpModalOpen}/>}
      {isLoggedIn ? <Homeview /> : <OtpModal isOpen={isOtpModalOpen} onClose={() => setOtpModalOpen(false)}  />}
    </div>
  );
}

export default App;
