import React, { useState } from 'react';

function TaskForm({ setShowModal }) {

    const getJwtToken = () => localStorage.getItem("access_token");

    const sendNewtask = async () => {
        try {
            const token = getJwtToken();

            if (!token) {
                throw new Error("No token found");
            }

            const response = await axios.get("http://127.0.0.1:8000/info", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (response.status === 200) {
                const data = response.data;
                setUserProfile((prevProfile) => ({
                    ...response.data,
                }));
            } else {
                throw new Error("User invalid");
            }
        } catch (error) {
            console.error("Error fetching info:", error);
        }
    };

    useEffect(() => {
        sendNewtask();
    }, []);

    const [task, setTask] = useState({
        taskID: "",
        name: "",
        description: "",
        dueDate: "",
        timeStart: "",
        timeEnd: "",
        allDay: false
    });

    const [allDay, setAllDay] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTask((prevTask) => ({
            ...prevTask,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Submitted Task:", task);
        setShowModal(false);
    };

    const handleClose = () => {
        setShowModal(false);
    };

    const handleAllDayChange = (e) => {
        const isChecked = e.target.checked;
        setAllDay(isChecked);

        if (isChecked) {
            setTask((prevTask) => ({
                ...prevTask,
                timeStart: '00:00',
                timeEnd: '23:59'
            }));
        } else {
            setTask((prevTask) => ({
                ...prevTask,
                timeStart: '',
                timeEnd: ''
            }));
        }
    };

    return (
        <>
            <div className="modal show" style={{ display: 'block' }} >
                <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                        <div className="modal-header d-flex justify-content-between">
                            <h5 className="modal-title">Create Task</h5>
                            <button type="button" className="close" onClick={handleClose}>
                                <span>&times;</span>
                            </button>
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="modal-body">
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Task Name</label>
                                    <input
                                        type="text"
                                        id="taskName"
                                        name="name"
                                        value={task.name}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Description</label>
                                    <textarea
                                        id="taskDescription"
                                        name="description"
                                        value={task.description}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Schedule Date</label>
                                    <input
                                        type="date"
                                        id="taskDueDate"
                                        name="dueDate"
                                        value={task.dueDate}
                                        onChange={handleChange}
                                        className="form-control"
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>Start Time</label>
                                    <input
                                        type="time"
                                        id="timeStart"
                                        name="timeStart"
                                        value={task.timeStart}
                                        onChange={handleChange}
                                        className="form-control"
                                        disabled={allDay} // Disable if allDay is checked
                                    />
                                </div>
                                <div className="form-group">
                                    <label className='d-flex justify-content-between m-2'>End Time</label>
                                    <input
                                        type="time"
                                        id="timeEnd"
                                        name="timeEnd"
                                        value={task.timeEnd}
                                        onChange={handleChange}
                                        className="form-control"
                                        disabled={allDay} // Disable if allDay is checked
                                    />
                                </div>
                                <div className="form-group d-flex align-items-center">
                                    <label className='d-flex m-2'>
                                    All Day
                                    </label>
                                    <input
                                        type="checkbox"
                                        id="allDay"
                                        name="allDay"
                                        checked={allDay}
                                        onChange={handleAllDayChange}
                                        className="form-check-input ms-2"
                                    />
                                </div>
                            </div>
                            <div className="modal-footer d-flex justify-content-between">
                                <button type="button" className="btn btn-secondary" onClick={handleClose}>Cancel</button>
                                <button type="submit" className="btn btn-primary">Create Task</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default TaskForm;
