import axios from 'axios';


function Navbar({ signup, signin, signoff, toggleSignup, toggleSignin }) {

const handleLogout = async () => {
    try {
        await axios.post('http://127.0.0.1:8000/logout/', {}, {
            headers: {
                Authorization: `Bearer ${getJwtToken()}`,
            },
        });
        localStorage.removeItem('access_token');

        // Update state to reflect logout
        setIsLoggedIn(false);
        setSignoff(false);
        setSignin(true);
        setSignup(false);
        setLanding(true);
    } catch (error) {
        console.error('Error logging out:', error);
        // Optionally show a message to the user about the error
    }
};
    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-body-tertiary">
                <div className="container-fluid">
                    <a className="navbar-brand brand fw-bold fs-3" href="#">
                        To-do-LIST
                    </a>
                    {signin && (
                        <a 
                            className="nav-link active fw-bold fs-5 brand-sub ps-4" 
                            aria-current="page" 
                            href="#"
                            onClick={toggleSignin}
                        >
                            Sign In
                        </a>
                    )}
                    {signup && (
                        <a 
                            className="nav-link active fw-bold fs-5 brand-sub ps-4" 
                            aria-current="page" 
                            href="#"
                            onClick={toggleSignup}
                        >
                            Sign Up
                        </a>
                    )}
                    {signoff && (
                        <a 
                            className="nav-link active fw-bold fs-5 ps-4"
                            aria-current="page" 
                            href="#" 
                            onClick={handleLogout} // Add onClick event
                        >
                            Log Out
                        </a>
                    )}
                </div>
            </nav>
        </div>
    );
}

export default Navbar;
