import React, { useEffect, useState } from "react";
import axios from "axios";

function Sidebar() {
    const [userProfile, setUserProfile] = useState({
        name: "Elon Musk",
        username: "@elonmusk",
        email: "elon@tesla.com",
        profilePic: "frontend_todo/public/images/bg.png",
    });

    const getJwtToken = () => localStorage.getItem("access_token");

    const getPersonalinfo = async () => {
        try {
            const token = getJwtToken();

            if (!token) {
                throw new Error("No token found");
            }

            const response = await axios.get("http://127.0.0.1:8000/info", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (response.status === 200) {
                const data = response.data;
                setUserProfile((prevProfile) => ({
                    ...response.data,
                }));
            } else {
                throw new Error("User invalid");
            }
        } catch (error) {
            console.error("Error fetching info:", error);
        }
    };

    useEffect(() => {
        getPersonalinfo();
    }, []);

    return (
        <div className="sidebar">
            <div className="profile-pic d-flex flex-column h-20">
                <img src="/images/bg.png" alt="Profile" />
            </div>
            <div className="user-info">
                <h4 className="name">{userProfile.name}</h4>
                <p className="username">{userProfile.username}</p>
                <p className="email">{userProfile.email}</p>
            </div>
            <div className="d-flex flex-column h-80">
                <div className="mt-auto mb-5">
                    <button className="btn btn-danger fw-bold d-flex justify-content-center">DELETE PROFILE</button>
                </div>
            </div>
        </div>
    );
}

export default Sidebar;
