import './App.css';
import Navbar from './navbar';
import Landing from './landingpage';
import { useEffect, useState } from 'react';
import OtpModal from './otp';
import Homeview from './homeview';
import axios from 'axios';
import { MakeAuthenticatedRequest } from './APIHelper';

function App() {
  const [signup, setSignup] = useState(false);
  const [signin, setSignin] = useState(true);
  const [signoff, setSignoff] = useState(false);
  const [isOtpModalOpen, setOtpModalOpen] = useState(false);
  const [landing, setLanding] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [Id, setId] = useState('');
  const [taskList, setTaskList] = useState([]);

  // const getJwtToken = () => localStorage.getItem('access_token');

  const checkAuthStatus = async () => {
    try {
      // const token = getJwtToken();

      // if (!token) {
      //   throw new Error('No token found');
      // }

      // const response = await axios.get('http://127.0.0.1:8000/', {
      //   headers: {
      //     Authorization: `Bearer ${token}`,
      //   },
      // });

      const response = await MakeAuthenticatedRequest('http://127.0.0.1:8000/');

      if (response.status === 200) {
        setIsLoggedIn(true);
        setSignoff(true);
        setSignin(false);
        setSignup(false);
        setLanding(false);
        setTaskList(response.data);
        console.log(response.data);
        console.log(Array.isArray(taskList));
      } else {
        throw new Error('Token invalid');
      }
    } catch (error) {
      console.error('Error checking login status:', error);
      setIsLoggedIn(false);
      setSignin(true);
      setSignup(false);
      setSignoff(false);
      setLanding(true);
    }
  };

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const toggleSignup = () => {
    setSignup(false);
    setSignin(true);
  };

  const toggleSignin = () => {
    setSignup(true);
    setSignin(false);
  };

  return (
    <div className="App">
    <Navbar 
        signup={signup} 
        signin={signin} 
        signoff={signoff} 
        toggleSignup={toggleSignup} 
        toggleSignin={toggleSignin}
        setIsLoggedIn={setIsLoggedIn} 
        setSignoff={setSignoff}       
        setSignin={setSignin}         
        setSignup={setSignup}         
        setLanding={setLanding}
      />
      {landing && (
        <Landing 
          signup={signup} 
          signin={signin} 
          setOtpModalOpen={setOtpModalOpen} 
          setId={setId}
        />
      )}
      {isLoggedIn ? (
        <Homeview taskList={taskList}/>
      ) : (
        <OtpModal 
          isOpen={isOtpModalOpen} 
          onClose={() => setOtpModalOpen(false)} 
          Id={Id} 
          setIsLoggedIn={setIsLoggedIn} 
          setSignoff={setSignoff} 
          setSignin={setSignin} 
          setSignup={setSignup} 
          setLanding={setLanding} 
        />
      )}
    </div>
  );
}

export default App;
