import logo from './logo.svg';
import './App.css';
import Navbar from './navbar';
import Signup from './signup';
import Signin from './signin';
import Sidebanner from './banner';
import { useState } from 'react';
import OtpModal from './OtpModal';

function App() {
  const [signup, setSignup] = useState(true);
  const [signin, setSignin] = useState(false);
  const [signoff, setSignoff] = useState(false);
  const [isOtpModalOpen, setOtpModalOpen] = useState(false); // State to manage OTP modal

  const toggleSignup = () => {
    setSignup(true);
    setSignin(false);
  };

  const toggleSignin = () => {
    setSignup(false);
    setSignin(true);
    setOtpModalOpen(true); // Open OTP modal when signing in
  };

  return (
    <div className="App">
      <Navbar 
        signup={signup} 
        signin={signin} 
        signoff={signoff} 
        toggleSignup={toggleSignup} 
        toggleSignin={toggleSignin} 
      />
      <div className='main_div'>
        <Sidebanner signup={signup} signin={signin}/>
        {signup && <Signup />}
        {signin && <Signin />}
      </div>
      <OtpModal isOpen={isOtpModalOpen} onClose={() => setOtpModalOpen(false)} />
    </div>
  );
}

export default App;
