import React from 'react';

function Sidebar() {

    const getJwtToken = () => localStorage.getItem('access_token');

    const getPersonalinfo = async () => {
      try {
        const token = getJwtToken();

        if (!token) {
          throw new Error('No token found');
        }

        const response = await axios.get('http://127.0.0.1:8000/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.status === 200) {
          setIsLoggedIn(true);
          setSignoff(true);
          setSignin(false);
          setSignup(false);
          setLanding(false);
        } else {
          throw new Error('Token invalid');
        }
      } catch (error) {
        console.error('Error checking login status:', error);
        setIsLoggedIn(false);
        setSignin(true);
        setSignup(false);
        setSignoff(false);
        setLanding(true);

        // Optional: Provide user feedback for specific errors
        // if (error.response) {
        //   if (error.response.status === 401) {
        //     alert('Session expired. Please log in again.'); // Notify user about session expiry
        //   } else {
        //     alert('An error occurred. Please try again later.'); // General error message
        //   }
        // }
      }
    };

    const userProfile = {
        name: "Elon Musk",
        username: "@elonmusk",
        email: "elon@tesla.com",
        profilePic: "https://via.placeholder.com/100"
    };

    return (
        <div className="sidebar">
            <div className="profile-pic">
                <img src={userProfile.profilePic} alt="Profile" />
            </div>
            <div className="user-info">
                <h4 className="name">{userProfile.name}</h4>
                <p className="username">{userProfile.username}</p>
                <p className="email">{userProfile.email}</p>
            </div>
        </div>
    );
}

export default Sidebar;
